// Константы для времени кэширования (TTL)

// Время кэширования в миллисекундах
export const CACHE_TTL = {
  // Активности - могут обновляться чаще
  ACTIVITIES: 30 * 60 * 1000, // 30 минут
  
  // Streams данные - редко меняются
  STREAMS: 7 * 24 * 60 * 60 * 1000, // 7 дней
  
  // Цели - зависят от streams, поэтому тоже 7 дней
  GOALS: 7 * 24 * 60 * 60 * 1000, // 7 дней
  
  // Погода - обновляется часто
  WEATHER: 30 * 60 * 1000, // 30 минут
  
  // Изображения - редко меняются
  IMAGES: 24 * 60 * 60 * 1000, // 24 часа
  
  // Последняя поездка - может обновляться
  LAST_RIDE: 60 * 60 * 1000, // 1 час
};

// Время очистки старых кэшей (должно быть больше TTL)
export const CLEANUP_TTL = {
  ACTIVITIES: 60 * 60 * 1000, // 1 час
  STREAMS: 10 * 24 * 60 * 60 * 1000, // 10 дней
  GOALS: 10 * 24 * 60 * 60 * 1000, // 10 дней
  WEATHER: 60 * 60 * 1000, // 1 час
  IMAGES: 2 * 24 * 60 * 60 * 1000, // 2 дня
  LAST_RIDE: 2 * 60 * 60 * 1000, // 2 часа
}; 